    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
                
        String nome = TextCampoNome.getText();
        String senha = String.valueOf(TextCampoSenha.getPassword());
        String idade = TextCampoIdade.getText();
        String endereco = TextCampoEndereco.getText();
        String cpf = TextCampoCPF.getText();
 
        
        Administrador adm = new Administrador( nome,  senha,  idade,  endereco,  cpf);
        
        int opcao = JOptionPane.showConfirmDialog(this,"Confirma cadastro?");
        
        if (opcao == JOptionPane.YES_OPTION){
            
            DAO dao = new DAO();
            try {
                dao.inserirAdmin(adm);
                JOptionPane.showMessageDialog(this, "Deu certo bro, gang gang");
            } catch (Exception ex) {
                JOptionPane.showMessageDialog(this, "Falha ao cadastrar admin");
            }
            
        }
    }//GEN-LAST:event_btnCadastrarActionPerformed

